import React, { useState } from "react";
import {
  ContainerCard,
  WrapperCards,
  CardSkill,
  CardsTitle,
  CardsPropContainer,
  CardSkillItems,
  CardTag,
  CardImg,
  StyledLink,
} from "./StylesCard";
import { Container, Item } from "../pagination/StylesPagination";
import { usePoke } from "../../../components/context/Provider";
import { MdSystemUpdateAlt } from "react-icons/md";
import ModelEdit from "../../modelEdit/ModelEdit";
import { Link } from "react-router-dom";
import { IoCloseSharp, IoCreateOutline } from "react-icons/io5";
import EditPokemon from "../../editPokemon/EditPokemon";
export default function Cards({
  nextPagePokemon,
  previousPagePokemon,
  actualPage,
}) {
  const { pokemonData, active } = usePoke();
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [sprite, setSprite] = useState("");
  const [attack, setAttack] = useState("");
  const [defense, setDefense] = useState("");
  const [backgroundColor, setBackgroundColor] = useState("");

  const [modal, setModal] = useState(true);

  const toggleModal = () => {
    setModal(!modal);
  };

  const history = useHistory();
  const { id } = useParams();

  console.log(id);
  useEffect(() => {
    api.get(`/pokemon/${id}`).then((response) => {
      console.log(response);
      setName(response.data);
      setType(response.data);
      setSprite(response.data);
      setAttack(response.data);
      setDefense(response.data);
      setBackgroundColor(response.data);
    });
  }, []);

  async function handleCreate(ev) {
    ev.preventDefault();
    const data = { name, type, sprite, attack, defense, backgroundColor };

    try {
      const response = await api.put(`/pokemon/${id}`, data);
      alert("Atualizaçao  realizado com sucesso");
      history.push("/Pokedex");
    } catch (err) {
      alert("Erro na atualizaço, tente novamente");
      console.log(err);
    }
  }


  return (
    <>
      <ContainerCard>
        {pokemonData.map((pokemon) => (
          <WrapperCards key={pokemon.id}>
            <CardSkill>
              <CardsTitle>{pokemon.name}</CardsTitle>
              <CardsPropContainer>
                <span className="firt-child">{pokemon.attack}</span>
                <span>{pokemon.defense}</span>
              </CardsPropContainer>

              <CardSkillItems>
                <span>Ataque</span>
                <span>Defesa</span>
              </CardSkillItems>

              <CardTag
                pokeTypeColor={pokemon.backgroundColor}
                josaph={pokemon.color}
              >
                <span>{pokemon.type}</span>
              </CardTag>
            </CardSkill>

            <CardImg backgroundColor={pokemon.backgroundColor}>
              <img src={pokemon.sprite} alt={pokemon.name} />
            </CardImg>

            <StyledLink to={`/edit/${pokemon.id}`}>
              <MdSystemUpdateAlt />
            </StyledLink>



            <>
        {modal && (
          <div className="modal">
            <div onClick={toggleModal} className="overlay"></div>
            <div className="modal-content">
              <div className="content-form">
                <form onSubmit={handleCreate}>
                  <div className="input-block">
                    <label htmlFor="name">Nome</label>
                    <input
                      id="name"
                      type="text"
                      name="name"
                      value={name.name}
                      onChange={(e) => setName(e.target.value)}
                    />
                  </div>

                  <div className="input-block">
                    <label htmlFor="sprite">Imagem</label>
                    <input
                      id="sprite"
                      name="sprite"
                      type="text"
                      value={sprite.sprite}
                      onChange={(e) => setSprite(e.target.value)}
                    />
                  </div>

                  <div className="input-block">
                    <label htmlFor="attack">Ataque</label>
                    <input
                      id="attack"
                      name="attack"
                      type="text"
                      value={attack.attack}
                      onChange={(e) => setAttack(e.target.value)}
                    />
                  </div>

                  <div className="input-block">
                    <label htmlFor="defense">Defesa</label>
                    <input
                      id="defense"
                      name="defense"
                      type="text"
                      value={defense.defense}
                      onChange={(e) => setDefense(e.target.value)}
                    />
                  </div>

                  <div className="content-type">
                    <select
                      onChange={(e) => setType(e.target.value)}
                      value={type.type}
                      className="selected-type"
                    >
                      <option value="water">water</option>
                      <option value="fire">fire</option>
                      <option value="normal">normal</option>
                      <option value="fighting">fighting</option>
                      <option value="flyig">flying</option>
                      <option value="poison">poison</option>
                      <option value="ground">ground</option>
                      <option value="rock">rock</option>
                      <option value="bug">bug</option>
                      <option value="ghost">ghost</option>
                      <option value="steel">steel</option>
                      <option value="grass">grass</option>
                      <option value="electric">electric</option>
                      <option value="psychic">psychic</option>
                      <option value="ice">ice</option>
                      <option value="dragon">dragon</option>
                      <option value="dark">dark</option>
                      <option value="fairy">fairy</option>
                      <option value="unknow">unknown</option>
                      <option value="shadow">shadow</option>
                    </select>
                    <div>
                      <IoIosArrowDown />
                    </div>
                  </div>

                  <div className="btn-content">
                    <button type="submit" className="input-btn">
                      Alterar
                    </button>
                  </div>
                </form>
              </div>

              <button className="close-modal" onClick={toggleModal}>
                <IoCloseSharp />
              </button>
            </div>
          </div>
        )}
      </>










































          </WrapperCards>
        ))}
      </ContainerCard>

      <Container>
        {actualPage - 1 === 0 ? (
          ""
        ) : (
          <Item onClick={previousPagePokemon}>{actualPage - 1}</Item>
        )}
        <Item isSelected={active}>{actualPage}</Item>
        <Item onClick={nextPagePokemon}>{actualPage + 1}</Item>
      </Container>
    </>
  );
}
