import React, { useState } from "react";
import {
  ContainerCard,
  WrapperCards,
  CardSkill,
  CardsTitle,
  CardsPropContainer,
  CardSkillItems,
  CardTag,
  CardImg,
  StyledLink
} from "./StylesCard";
import { Container, Item } from "../pagination/StylesPagination";
import { usePoke } from "../../../components/context/Provider";
import { MdSystemUpdateAlt } from "react-icons/md";
import ModelEdit from "../../modelEdit/ModelEdit";
export default function Cards({
  nextPagePokemon,
  previousPagePokemon,
  actualPage,
}) {

  const [modal, setModal] = useState(false);

  const { pokemonData } = usePoke();
  const toggleModal = () => {
    setModal(!modal);
  };

  if (modal) {
    document.body.classList.add("active-modal");
  } else {
    document.body.classList.remove("active-modal");
  }

  return (
    <>
      <ContainerCard>
        {pokemonsData.map((pokemon) => (
          <WrapperCards key={pokemon.id}>
            <CardSkill>
              <CardsTitle>{pokemon.name}</CardsTitle>
              <CardsPropContainer>
                <span className="firt-child">{pokemon.attack}</span>
                <span>{pokemon.defense}</span>
              </CardsPropContainer>

              <CardSkillItems>
                <span>
                  Ataque
                </span>
                <span>
                  Defesa
                </span>
              </CardSkillItems>

              <CardTag
                pokeTypeColor={pokemon.backgroundColor}
                josaph={pokemon.color}
              >
                <span>{pokemon.type}</span>
              </CardTag>
            </CardSkill>

            <CardImg backgroundColor={pokemon.backgroundColor}>
              <img src={pokemon.sprite} alt={pokemon.name} />
            </CardImg>

            <StyledLink to={`/edit/${pokemon.id}`}>
              <MdSystemUpdateAlt />
            </StyledLink>




            <>
      <button onClick={toggleModal} className="btn-modal">

          OPEN

      </button>

      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
          <div className="modal-content">
            <EditPokemon />

            <button className="close-modal" onClick={toggleModal}>
              <IoCloseSharp />
            </button>
          </div>
        </div>
      )}
    </>





























            <ModelEdit/>


          </WrapperCards>
        ))}
      </ContainerCard>

      <Container>
        {actualPage - 1 === 0 ? (
          ""
        ) : (
          <Item onClick={previousPagePokemon}>{actualPage - 1}</Item>
        )}
        <Item isSelected={active}>{actualPage}</Item>
        <Item onClick={nextPagePokemon}>{actualPage + 1}</Item>
      </Container>
    </>
  );
}
